SELECT MIN("lineorder"."lo_ordertotalprice" + "part"."p_size") as agg_0 FROM "part" LEFT OUTER JOIN "lineorder" ON "part"."p_partkey" = "lineorder"."lo_partkey"  WHERE "lineorder"."lo_linenumber" BETWEEN 1 AND 3 AND "part"."p_name" IN ('aquamarine firebrick', 'beige blue') AND "lineorder"."lo_extendedprice" BETWEEN 1627798 AND 10149914 AND "lineorder"."lo_commitdate" BETWEEN 19943436 AND 19944605 AND "part"."p_partkey" BETWEEN 311865 AND 385645