SELECT MAX("dim_date"."d_daynuminweek") as agg_0 FROM "lineorder" LEFT OUTER JOIN "supplier" ON "lineorder"."lo_suppkey" = "supplier"."s_suppkey" LEFT OUTER JOIN "dim_date" ON "lineorder"."lo_orderdate" = "dim_date"."d_datekey"  WHERE "lineorder"."lo_suppkey" >= 3971 AND "dim_date"."d_yearmonth" IN ('Jan1996', 'Dec1994', 'Jan1992')