SELECT MIN("lineorder"."lo_ordertotalprice" + "dim_date"."d_lastdayinweekfl") as agg_0, AVG("lineorder"."lo_custkey" + "dim_date"."d_weeknuminyear") as agg_1, COUNT(*) as agg_2 FROM "supplier" LEFT OUTER JOIN "lineorder" ON "supplier"."s_suppkey" = "lineorder"."lo_suppkey" LEFT OUTER JOIN "part" ON "lineorder"."lo_partkey" = "part"."p_partkey" LEFT OUTER JOIN "dim_date" ON "lineorder"."lo_orderdate" = "dim_date"."d_datekey" LEFT OUTER JOIN "customer" ON "lineorder"."lo_custkey" = "customer"."c_custkey"  WHERE "part"."p_catery" = 'MFGR#13' AND "supplier"."s_suppkey" BETWEEN 1852 AND 3480 AND "customer"."c_city" != 'ALGERIA  8'