SELECT MIN("part"."p_partkey") as agg_0, AVG("dim_date"."d_daynuminyear") as agg_1, COUNT(*) as agg_2 FROM "part" LEFT OUTER JOIN "lineorder" ON "part"."p_partkey" = "lineorder"."lo_partkey" LEFT OUTER JOIN "dim_date" ON "lineorder"."lo_orderdate" = "dim_date"."d_datekey" LEFT OUTER JOIN "supplier" ON "lineorder"."lo_suppkey" = "supplier"."s_suppkey" LEFT OUTER JOIN "customer" ON "lineorder"."lo_custkey" = "customer"."c_custkey"  WHERE "part"."p_color" NOT LIKE '%cornsilk%' AND "customer"."c_mktsegment" NOT LIKE '%FURNITURE%'