SELECT COUNT(*) as agg_0, COUNT(*) as agg_1, AVG("lineorder"."lo_commitdate" + "lineorder"."lo_partkey") as agg_2 FROM "part" LEFT OUTER JOIN "lineorder" ON "part"."p_partkey" = "lineorder"."lo_partkey" LEFT OUTER JOIN "dim_date" ON "lineorder"."lo_orderdate" = "dim_date"."d_datekey" LEFT OUTER JOIN "customer" ON "lineorder"."lo_custkey" = "customer"."c_custkey" LEFT OUTER JOIN "supplier" ON "lineorder"."lo_suppkey" = "supplier"."s_suppkey"  WHERE "dim_date"."d_daynuminmonth" <= 7 AND "customer"."c_city" LIKE '%SAUDI%' AND "part"."p_catery" LIKE '%MFGR#25%'