SELECT MAX("lineorder"."lo_linenumber" + "lineorder"."lo_commitdate") as agg_0, MIN("lineorder"."lo_linenumber" + "lineorder"."lo_quantity") as agg_1 FROM "part" LEFT OUTER JOIN "lineorder" ON "part"."p_partkey" = "lineorder"."lo_partkey" LEFT OUTER JOIN "supplier" ON "lineorder"."lo_suppkey" = "supplier"."s_suppkey"  WHERE "lineorder"."lo_shippriority" >= 0 AND "supplier"."s_suppkey" BETWEEN 954 AND 1228