SELECT SUM("part"."p_size") as agg_0, AVG("part"."p_partkey" + "part"."p_size") as agg_1, AVG("part"."p_size") as agg_2 FROM "part"  WHERE "part"."p_color" NOT LIKE '%o%range%' AND "part"."p_type" LIKE '%ECO%NOMY%' AND ("part"."p_name" IN ('chartreuse black', 'burnished azure', 'aquamarine firebrick', 'aquamarine blue', 'bisque blush', 'aquamarine black', 'beige blue') OR "part"."p_size" >= 6 OR "part"."p_name" IN ('chartreuse black')) AND "part"."p_catery" IN ('MFGR#35', 'MFGR#53', 'MFGR#41', 'MFGR#13', 'MFGR#23', 'MFGR#51', 'MFGR#54') AND "part"."p_type" LIKE '%BR%ASS%'