SELECT AVG("lineorder"."lo_extendedprice" + "lineorder"."lo_commitdate") as agg_0, AVG("dim_date"."d_yearmonthnum") as agg_1 FROM "supplier" LEFT OUTER JOIN "lineorder" ON "supplier"."s_suppkey" = "lineorder"."lo_suppkey" LEFT OUTER JOIN "part" ON "lineorder"."lo_partkey" = "part"."p_partkey" LEFT OUTER JOIN "dim_date" ON "lineorder"."lo_orderdate" = "dim_date"."d_datekey"  WHERE ("lineorder"."lo_shipmode" NOT LIKE '%TRUCK%' OR "lineorder"."lo_shipmode" IN ('MAIL', 'SHIP', 'TRUCK', 'REG AIR', 'RAIL')) AND "part"."p_type" NOT LIKE '%MEDIUM%' AND "part"."p_type" IN ('MEDIUM PLATED STEEL', 'ECONOMY BRUSHED TIN', 'SMALL BRUSHED NICKEL', 'SMALL PLATED NICKEL', 'SMALL ANODIZED TIN', 'ECONOMY ANODIZED NICKEL', 'MEDIUM ANODIZED TIN', 'ECONOMY ANODIZED STEEL')