SELECT AVG("lineorder"."lo_custkey") as agg_0, MIN("lineorder"."lo_suppkey") as agg_1, AVG("lineorder"."lo_discount" + "lineorder"."lo_ordertotalprice") as agg_2 FROM "supplier" LEFT OUTER JOIN "lineorder" ON "supplier"."s_suppkey" = "lineorder"."lo_suppkey"  WHERE "lineorder"."lo_quantity" BETWEEN 1 AND 16 AND ("lineorder"."lo_shipmode" LIKE '%MA%IL%' OR "lineorder"."lo_discount" <= 5)