SELECT COUNT(*) as agg_0 FROM "lineorder" LEFT OUTER JOIN "supplier" ON "lineorder"."lo_suppkey" = "supplier"."s_suppkey" LEFT OUTER JOIN "customer" ON "lineorder"."lo_custkey" = "customer"."c_custkey" LEFT OUTER JOIN "dim_date" ON "lineorder"."lo_orderdate" = "dim_date"."d_datekey" LEFT OUTER JOIN "part" ON "lineorder"."lo_partkey" = "part"."p_partkey"  WHERE "supplier"."s_region" NOT LIKE '%EUR%OPE%' OR ("supplier"."s_region" IN ('MIDDLE EAST') AND "supplier"."s_region" != 'MIDDLE EAST')