SELECT MAX("dim_date"."d_weeknuminyear") as agg_0, COUNT(*) as agg_1, AVG("dim_date"."d_weekdayfl" + "dim_date"."d_yearmonthnum") as agg_2 FROM "dim_date"  WHERE "dim_date"."d_daynuminmonth" >= 2 AND "dim_date"."d_weekdayfl" <= 1 AND ("dim_date"."d_yearmonth" LIKE '%Feb1%998%' OR "dim_date"."d_lastdayinmonthfl" <= 1 OR "dim_date"."d_yearmonth" LIKE '%Jul1994%') AND "dim_date"."d_date" = 'December 28, 1998'