SELECT MIN("part"."p_size") as agg_0, MIN("customer"."c_custkey" + "lineorder"."lo_discount") as agg_1 FROM "supplier" LEFT OUTER JOIN "lineorder" ON "supplier"."s_suppkey" = "lineorder"."lo_suppkey" LEFT OUTER JOIN "customer" ON "lineorder"."lo_custkey" = "customer"."c_custkey" LEFT OUTER JOIN "dim_date" ON "lineorder"."lo_orderdate" = "dim_date"."d_datekey" LEFT OUTER JOIN "part" ON "lineorder"."lo_partkey" = "part"."p_partkey"  WHERE "customer"."c_nation" IN ('UNITED STATES', 'JAPAN', 'INDONESIA', 'CHINA', 'MOZAMBIQUE', 'CANADA') AND "lineorder"."lo_shipmode" != 'TRUCK' AND "part"."p_container" NOT LIKE '%LG%' AND "part"."p_type" LIKE '%BRUSHED%' AND "dim_date"."d_daynuminmonth" != 19 AND "dim_date"."d_monthnuminyear" != 10