SELECT SUM("lineorder"."lo_partkey") as agg_0, SUM("lineorder"."lo_partkey" + "lineorder"."lo_commitdate") as agg_1 FROM "dim_date" LEFT OUTER JOIN "lineorder" ON "dim_date"."d_datekey" = "lineorder"."lo_orderdate" LEFT OUTER JOIN "supplier" ON "lineorder"."lo_suppkey" = "supplier"."s_suppkey"  WHERE "dim_date"."d_dayofweek" IN ('Friday', 'Thursday', 'Wednesday', 'Tuesday') AND "lineorder"."lo_shipmode" IN ('SHIP', 'RAIL')