SELECT AVG("lineorder"."lo_quantity" + "dim_date"."d_daynuminyear") as agg_0, COUNT(*) as agg_1 FROM "dim_date" LEFT OUTER JOIN "lineorder" ON "dim_date"."d_datekey" = "lineorder"."lo_orderdate" LEFT OUTER JOIN "supplier" ON "lineorder"."lo_suppkey" = "supplier"."s_suppkey" LEFT OUTER JOIN "part" ON "lineorder"."lo_partkey" = "part"."p_partkey" LEFT OUTER JOIN "customer" ON "lineorder"."lo_custkey" = "customer"."c_custkey"  WHERE "lineorder"."lo_orderpriority" != '3-MEDIUM' AND ("lineorder"."lo_linenumber" >= 5 OR ("lineorder"."lo_linenumber" = 6 AND "lineorder"."lo_linenumber" <= 2))