SELECT MAX("lineorder"."lo_revenue") as agg_0, SUM("customer"."c_custkey" + "lineorder"."lo_ordertotalprice") as agg_1, AVG("lineorder"."lo_discount") as agg_2 FROM "supplier" LEFT OUTER JOIN "lineorder" ON "supplier"."s_suppkey" = "lineorder"."lo_suppkey" LEFT OUTER JOIN "customer" ON "lineorder"."lo_custkey" = "customer"."c_custkey"  WHERE "lineorder"."lo_partkey" BETWEEN 115784 AND 124326 AND "customer"."c_city" != 'UNITED KI1' AND "supplier"."s_city" LIKE '%PERU%'