SELECT MIN("lineorder"."lo_orderkey" + "lineorder"."lo_ordertotalprice") as agg_0 FROM "dim_date" LEFT OUTER JOIN "lineorder" ON "dim_date"."d_datekey" = "lineorder"."lo_orderdate" LEFT OUTER JOIN "supplier" ON "lineorder"."lo_suppkey" = "supplier"."s_suppkey" LEFT OUTER JOIN "customer" ON "lineorder"."lo_custkey" = "customer"."c_custkey"  WHERE ("supplier"."s_city" = 'IRAN     4' OR ("supplier"."s_city" NOT LIKE '%ROMA%NIA%' AND "supplier"."s_city" = 'EGYPT    5')) AND "lineorder"."lo_shipmode" NOT LIKE '%M%AIL%' AND ("customer"."c_nation" IN ('JAPAN', 'KENYA', 'CANADA', 'RUSSIA') OR ("customer"."c_city" NOT LIKE '%U%NITED%' AND "customer"."c_nation" = 'ARGENTINA')) AND "customer"."c_nation" IN ('RUSSIA', 'CANADA', 'MOZAMBIQUE', 'IRAQ', 'ARGENTINA', 'CHINA')