SELECT SUM("lineorder"."lo_orderdate" + "lineorder"."lo_ordertotalprice") as agg_0, COUNT(*) as agg_1 FROM "supplier" LEFT OUTER JOIN "lineorder" ON "supplier"."s_suppkey" = "lineorder"."lo_suppkey" LEFT OUTER JOIN "customer" ON "lineorder"."lo_custkey" = "customer"."c_custkey"  WHERE "supplier"."s_region" IN ('MIDDLE EAST', 'EUROPE') AND "supplier"."s_nation" IN ('INDONESIA', 'CANADA')