SELECT MAX("part"."p_partkey") as agg_0, MAX("lineorder"."lo_orderdate") as agg_1, AVG("lineorder"."lo_partkey") as agg_2 FROM "part" LEFT OUTER JOIN "lineorder" ON "part"."p_partkey" = "lineorder"."lo_partkey" LEFT OUTER JOIN "supplier" ON "lineorder"."lo_suppkey" = "supplier"."s_suppkey"  WHERE "part"."p_size" >= 16 AND "supplier"."s_phone" = '18-643-275-8135' AND "part"."p_mfgr" IN ('MFGR#2')