SELECT AVG("lineorder"."lo_partkey" + "dim_date"."d_holidayfl") as agg_0, MAX("part"."p_partkey" + "lineorder"."lo_linenumber") as agg_1, SUM("lineorder"."lo_orderdate") as agg_2 FROM "dim_date" LEFT OUTER JOIN "lineorder" ON "dim_date"."d_datekey" = "lineorder"."lo_orderdate" LEFT OUTER JOIN "part" ON "lineorder"."lo_partkey" = "part"."p_partkey" LEFT OUTER JOIN "customer" ON "lineorder"."lo_custkey" = "customer"."c_custkey" LEFT OUTER JOIN "supplier" ON "lineorder"."lo_suppkey" = "supplier"."s_suppkey"  WHERE "dim_date"."d_yearmonth" != 'Mar1993' AND "supplier"."s_region" NOT LIKE '%AFRI%CA%' AND "supplier"."s_nation" != 'UNITED STATES' AND "customer"."c_region" = 'AMERICA'