SELECT COUNT(*) as agg_0, AVG("lineorder"."lo_linenumber") as agg_1, MAX("lineorder"."lo_suppkey" + "lineorder"."lo_tax") as agg_2 FROM "lineorder" LEFT OUTER JOIN "dim_date" ON "lineorder"."lo_orderdate" = "dim_date"."d_datekey" LEFT OUTER JOIN "supplier" ON "lineorder"."lo_suppkey" = "supplier"."s_suppkey" LEFT OUTER JOIN "customer" ON "lineorder"."lo_custkey" = "customer"."c_custkey" LEFT OUTER JOIN "part" ON "lineorder"."lo_partkey" = "part"."p_partkey"  WHERE "supplier"."s_region" IN ('EUROPE') AND "supplier"."s_region" != 'ASIA' AND "lineorder"."lo_extendedprice" BETWEEN 3442536 AND 6174869