SELECT MIN("lineorder"."lo_tax") as agg_0, AVG("lineorder"."lo_supplycost" + "lineorder"."lo_revenue") as agg_1, MIN("lineorder"."lo_revenue" + "lineorder"."lo_partkey") as agg_2 FROM "lineorder"  WHERE "lineorder"."lo_orderdate" <= 19961821 AND "lineorder"."lo_quantity" <= 44 AND "lineorder"."lo_shipmode" IN ('MAIL', 'TRUCK', 'SHIP', 'FOB', 'REG AIR', 'RAIL') AND "lineorder"."lo_tax" >= 0 AND "lineorder"."lo_supplycost" BETWEEN 76886 AND 100047