SELECT MAX("customer"."c_custkey") as agg_0, MIN("customer"."c_custkey") as agg_1, SUM("customer"."c_custkey") as agg_2 FROM "customer"  WHERE "customer"."c_city" LIKE '%CANADA%' AND "customer"."c_custkey" BETWEEN 46479 AND 46962 AND "customer"."c_region" IN ('MIDDLE EAST', 'AFRICA') AND ("customer"."c_mktsegment" NOT LIKE '%MACHIN%ERY%' OR "customer"."c_mktsegment" NOT LIKE '%AUTOM%OBILE%') AND "customer"."c_city" NOT LIKE '%KENYA%'