SELECT COUNT(*) as agg_0 FROM "customer" LEFT OUTER JOIN "lineorder" ON "customer"."c_custkey" = "lineorder"."lo_custkey" LEFT OUTER JOIN "part" ON "lineorder"."lo_partkey" = "part"."p_partkey" LEFT OUTER JOIN "supplier" ON "lineorder"."lo_suppkey" = "supplier"."s_suppkey" LEFT OUTER JOIN "dim_date" ON "lineorder"."lo_orderdate" = "dim_date"."d_datekey"  WHERE ("part"."p_color" NOT LIKE '%hot%' OR "part"."p_mfgr" IN ('MFGR#5', 'MFGR#1', 'MFGR#2')) AND "dim_date"."d_dayofweek" = 'Tuesday' AND "supplier"."s_suppkey" <= 2079 AND "dim_date"."d_holidayfl" BETWEEN 0 AND 1 AND "customer"."c_region" IN ('EUROPE', 'MIDDLE EAST', 'ASIA', 'AMERICA') AND "part"."p_size" = 11