SELECT SUM("dim_date"."d_daynuminweek") as agg_0 FROM "customer" LEFT OUTER JOIN "lineorder" ON "customer"."c_custkey" = "lineorder"."lo_custkey" LEFT OUTER JOIN "dim_date" ON "lineorder"."lo_orderdate" = "dim_date"."d_datekey"  WHERE "customer"."c_region" LIKE '%EUROPE%' AND "customer"."c_city" IN ('FRANCE   5', 'IRAQ     6', 'UNITED ST6', 'MOZAMBIQU4', 'INDIA    4', 'MOROCCO  8', 'ROMANIA  4', 'MOZAMBIQU8', 'JAPAN    5') AND ("dim_date"."d_date" IN ('Augest 29, 1996') OR "dim_date"."d_daynuminyear" <= 162)