SELECT AVG("part"."p_size" + "lineorder"."lo_custkey") as agg_0 FROM "customer" LEFT OUTER JOIN "lineorder" ON "customer"."c_custkey" = "lineorder"."lo_custkey" LEFT OUTER JOIN "part" ON "lineorder"."lo_partkey" = "part"."p_partkey"  WHERE "customer"."c_region" NOT LIKE '%MIDDLE%' AND "part"."p_type" IN ('ECONOMY BRUSHED TIN', 'SMALL ANODIZED COPPER') AND "customer"."c_nation" IN ('CHINA', 'RUSSIA', 'KENYA', 'JAPAN', 'ARGENTINA', 'INDONESIA') AND "customer"."c_city" != 'JORDAN   1'