SELECT MAX("lineorder"."lo_orderdate") as agg_0, COUNT(*) as agg_1, SUM("lineorder"."lo_shippriority" + "lineorder"."lo_discount") as agg_2 FROM "supplier" LEFT OUTER JOIN "lineorder" ON "supplier"."s_suppkey" = "lineorder"."lo_suppkey" LEFT OUTER JOIN "part" ON "lineorder"."lo_partkey" = "part"."p_partkey"  WHERE ("lineorder"."lo_orderpriority" IN ('5-LOW', '1-URGENT', '3-MEDIUM') OR "lineorder"."lo_commitdate" >= 19948764 OR "lineorder"."lo_orderpriority" = '5-LOW') AND ("part"."p_type" LIKE '%POLISH%ED%' OR ("part"."p_mfgr" != 'MFGR#4' AND "part"."p_type" IN ('MEDIUM PLATED STEEL', 'ECONOMY ANODIZED STEEL')))