SELECT MIN("dim_date"."d_lastdayinweekfl") as agg_0, AVG("lineorder"."lo_ordertotalprice" + "dim_date"."d_daynuminweek") as agg_1 FROM "dim_date" LEFT OUTER JOIN "lineorder" ON "dim_date"."d_datekey" = "lineorder"."lo_orderdate" LEFT OUTER JOIN "supplier" ON "lineorder"."lo_suppkey" = "supplier"."s_suppkey" LEFT OUTER JOIN "part" ON "lineorder"."lo_partkey" = "part"."p_partkey" LEFT OUTER JOIN "customer" ON "lineorder"."lo_custkey" = "customer"."c_custkey"  WHERE "dim_date"."d_date" != 'January 18, 1995' AND "customer"."c_mktsegment" NOT LIKE '%BUILDING%' AND "dim_date"."d_month" LIKE '%Octorber%'