SELECT MIN("dim_date"."d_holidayfl") as agg_0, COUNT(*) as agg_1, AVG("dim_date"."d_holidayfl" + "lineorder"."lo_orderkey") as agg_2 FROM "dim_date" LEFT OUTER JOIN "lineorder" ON "dim_date"."d_datekey" = "lineorder"."lo_orderdate" LEFT OUTER JOIN "supplier" ON "lineorder"."lo_suppkey" = "supplier"."s_suppkey"  WHERE ("lineorder"."lo_shipmode" NOT LIKE '%FOB%' OR "lineorder"."lo_orderdate" >= 19930668 OR "lineorder"."lo_shipmode" LIKE '%TRUCK%') AND ("lineorder"."lo_extendedprice" >= 1625198 OR "lineorder"."lo_discount" != 2) AND ("dim_date"."d_monthnuminyear" BETWEEN 2 AND 11 OR "dim_date"."d_monthnuminyear" >= 11 OR "dim_date"."d_monthnuminyear" != 6) AND ("lineorder"."lo_revenue" BETWEEN 1602476 AND 2061521 OR "lineorder"."lo_revenue" >= 6284627) AND "dim_date"."d_daynuminmonth" = 27