SELECT COUNT(*) as agg_0, COUNT(*) as agg_1, AVG("lineorder"."lo_commitdate" + "part"."p_partkey") as agg_2 FROM "customer" LEFT OUTER JOIN "lineorder" ON "customer"."c_custkey" = "lineorder"."lo_custkey" LEFT OUTER JOIN "dim_date" ON "lineorder"."lo_orderdate" = "dim_date"."d_datekey" LEFT OUTER JOIN "part" ON "lineorder"."lo_partkey" = "part"."p_partkey"  WHERE "dim_date"."d_dayofweek" = 'Wednesday' AND ("dim_date"."d_month" LIKE '%N%ovember%' OR "dim_date"."d_lastdayinweekfl" <= 1) AND ("part"."p_container" NOT LIKE '%JAR%' OR ("part"."p_type" LIKE '%LARGE%' AND "part"."p_container" = 'JUMBO BAG')) AND "dim_date"."d_yearmonth" NOT LIKE '%De%c1995%'