SELECT MAX("lineorder"."lo_ordertotalprice") as agg_0, MAX("lineorder"."lo_ordertotalprice" + "lineorder"."lo_custkey") as agg_1 FROM "customer" LEFT OUTER JOIN "lineorder" ON "customer"."c_custkey" = "lineorder"."lo_custkey"  WHERE "customer"."c_custkey" >= 24015 AND ("lineorder"."lo_orderpriority" != '1-URGENT' OR "lineorder"."lo_orderpriority" NOT LIKE '%SPECIFIED%' OR "lineorder"."lo_orderpriority" NOT LIKE '%1%-URGENT%')