SELECT AVG("lineorder"."lo_partkey") as agg_0 FROM "dim_date" LEFT OUTER JOIN "lineorder" ON "dim_date"."d_datekey" = "lineorder"."lo_orderdate" LEFT OUTER JOIN "supplier" ON "lineorder"."lo_suppkey" = "supplier"."s_suppkey" LEFT OUTER JOIN "customer" ON "lineorder"."lo_custkey" = "customer"."c_custkey"  WHERE ("dim_date"."d_yearmonth" LIKE '%Jul1994%' OR "dim_date"."d_daynuminweek" != 7 OR "dim_date"."d_yearmonth" LIKE '%Jan1995%') AND "lineorder"."lo_quantity" BETWEEN 16 AND 27 AND "dim_date"."d_daynuminmonth" >= 24 AND "supplier"."s_name" IN ('Supplier#000002662', 'Supplier#000002672', 'Supplier#000002665', 'Supplier#000002660', 'Supplier#000002663', 'Supplier#000002659', 'Supplier#000000001', 'Supplier#000002664', 'Supplier#000002661') AND "lineorder"."lo_shippriority" BETWEEN 0 AND 1 AND "customer"."c_nation" != 'UNITED STATES'