SELECT AVG("supplier"."s_suppkey" + "lineorder"."lo_partkey") as agg_0 FROM "lineorder" LEFT OUTER JOIN "dim_date" ON "lineorder"."lo_orderdate" = "dim_date"."d_datekey" LEFT OUTER JOIN "supplier" ON "lineorder"."lo_suppkey" = "supplier"."s_suppkey" LEFT OUTER JOIN "part" ON "lineorder"."lo_partkey" = "part"."p_partkey"  WHERE "dim_date"."d_yearmonth" LIKE '%Dec1996%'