SELECT MIN("dim_date"."d_lastdayinmonthfl") as agg_0, COUNT(*) as agg_1 FROM "lineorder" LEFT OUTER JOIN "dim_date" ON "lineorder"."lo_orderdate" = "dim_date"."d_datekey" LEFT OUTER JOIN "part" ON "lineorder"."lo_partkey" = "part"."p_partkey" LEFT OUTER JOIN "customer" ON "lineorder"."lo_custkey" = "customer"."c_custkey" LEFT OUTER JOIN "supplier" ON "lineorder"."lo_suppkey" = "supplier"."s_suppkey"  WHERE "lineorder"."lo_revenue" BETWEEN 3513753 AND 7776671 AND "part"."p_type" NOT LIKE '%LAR%GE%'