SELECT MIN("lineorder"."lo_commitdate" + "dim_date"."d_holidayfl") as agg_0 FROM "part" LEFT OUTER JOIN "lineorder" ON "part"."p_partkey" = "lineorder"."lo_partkey" LEFT OUTER JOIN "customer" ON "lineorder"."lo_custkey" = "customer"."c_custkey" LEFT OUTER JOIN "dim_date" ON "lineorder"."lo_orderdate" = "dim_date"."d_datekey" LEFT OUTER JOIN "supplier" ON "lineorder"."lo_suppkey" = "supplier"."s_suppkey"  WHERE "lineorder"."lo_shipmode" LIKE '%RE%G%' AND "dim_date"."d_date" LIKE '%2%4,%' AND "dim_date"."d_month" IN ('May', 'December', 'June', 'March')