SELECT COUNT(*) as agg_0, COUNT(*) as agg_1 FROM "supplier" LEFT OUTER JOIN "lineorder" ON "supplier"."s_suppkey" = "lineorder"."lo_suppkey" LEFT OUTER JOIN "part" ON "lineorder"."lo_partkey" = "part"."p_partkey" LEFT OUTER JOIN "dim_date" ON "lineorder"."lo_orderdate" = "dim_date"."d_datekey"  WHERE "lineorder"."lo_shipmode" NOT LIKE '%FOB%' AND "part"."p_color" LIKE '%light%' AND "part"."p_catery" LIKE '%MFG%R#42%' AND "part"."p_mfgr" = 'MFGR#1' AND ("part"."p_name" IN ('aquamarine firebrick', 'azure beige', 'burnished azure', 'chartreuse black', 'beige blue') OR "part"."p_name" LIKE '%bl%ue%' OR "part"."p_name" LIKE '%blue%')