SELECT COUNT(*) as agg_0, MIN("dim_date"."d_monthnuminyear" + "dim_date"."d_weeknuminyear") as agg_1, AVG("dim_date"."d_datekey" + "lineorder"."lo_orderdate") as agg_2 FROM "part" LEFT OUTER JOIN "lineorder" ON "part"."p_partkey" = "lineorder"."lo_partkey" LEFT OUTER JOIN "supplier" ON "lineorder"."lo_suppkey" = "supplier"."s_suppkey" LEFT OUTER JOIN "dim_date" ON "lineorder"."lo_orderdate" = "dim_date"."d_datekey" LEFT OUTER JOIN "customer" ON "lineorder"."lo_custkey" = "customer"."c_custkey"  WHERE ("lineorder"."lo_orderpriority" NOT LIKE '%3-MEDIUM%' OR "lineorder"."lo_orderpriority" NOT LIKE '%SPECIFIED%') AND "part"."p_size" = 7 AND "supplier"."s_name" != 'Supplier#000001086' AND "customer"."c_city" IN ('IRAQ     6', 'JAPAN    5', 'MOZAMBIQU4', 'MOZAMBIQU8', 'INDIA    5') AND "lineorder"."lo_shipmode" LIKE '%TRUCK%'