SELECT AVG("dim_date"."d_holidayfl") as agg_0, SUM("lineorder"."lo_shippriority" + "part"."p_partkey") as agg_1 FROM "lineorder" LEFT OUTER JOIN "supplier" ON "lineorder"."lo_suppkey" = "supplier"."s_suppkey" LEFT OUTER JOIN "part" ON "lineorder"."lo_partkey" = "part"."p_partkey" LEFT OUTER JOIN "customer" ON "lineorder"."lo_custkey" = "customer"."c_custkey" LEFT OUTER JOIN "dim_date" ON "lineorder"."lo_orderdate" = "dim_date"."d_datekey"  WHERE "dim_date"."d_month" NOT LIKE '%November%' AND "part"."p_mfgr" NOT LIKE '%MFG%R#1%' AND "lineorder"."lo_shipmode" != 'REG AIR'