SELECT COUNT(*) as agg_0, AVG("lineorder"."lo_linenumber") as agg_1, AVG("dim_date"."d_year") as agg_2 FROM "supplier" LEFT OUTER JOIN "lineorder" ON "supplier"."s_suppkey" = "lineorder"."lo_suppkey" LEFT OUTER JOIN "dim_date" ON "lineorder"."lo_orderdate" = "dim_date"."d_datekey"  WHERE "supplier"."s_city" NOT LIKE '%JAPA%N%' AND "dim_date"."d_lastdayinweekfl" BETWEEN 0 AND 1 AND "supplier"."s_region" NOT LIKE '%EAST%' AND ("lineorder"."lo_shipmode" LIKE '%MAIL%' OR ("lineorder"."lo_shipmode" IN ('AIR') AND "lineorder"."lo_shipmode" NOT LIKE '%AI%R%')) AND "lineorder"."lo_revenue" >= 3765167