SELECT COUNT(*) as agg_0, MAX("lineorder"."lo_suppkey") as agg_1 FROM "customer" LEFT OUTER JOIN "lineorder" ON "customer"."c_custkey" = "lineorder"."lo_custkey"  WHERE ("lineorder"."lo_orderpriority" IN ('4-NOT SPECIFIED', '3-MEDIUM') OR ("lineorder"."lo_orderkey" <= 6364960 AND "lineorder"."lo_orderpriority" NOT LIKE '%1-%URGENT%')) AND ("lineorder"."lo_shipmode" LIKE '%RAIL%' OR "lineorder"."lo_shipmode" LIKE '%AI%R%') AND "customer"."c_region" IN ('AFRICA') AND ("lineorder"."lo_commitdate" <= 19925233 OR "lineorder"."lo_commitdate" >= 19961548) AND "customer"."c_region" LIKE '%AF%RICA%' AND "customer"."c_mktsegment" IN ('HOUSEHOLD', 'BUILDING', 'AUTOMOBILE')