SELECT MAX("dim_date"."d_daynuminmonth" + "part"."p_partkey") as agg_0, MIN("dim_date"."d_daynuminyear") as agg_1, SUM("dim_date"."d_holidayfl") as agg_2 FROM "supplier" LEFT OUTER JOIN "lineorder" ON "supplier"."s_suppkey" = "lineorder"."lo_suppkey" LEFT OUTER JOIN "part" ON "lineorder"."lo_partkey" = "part"."p_partkey" LEFT OUTER JOIN "customer" ON "lineorder"."lo_custkey" = "customer"."c_custkey" LEFT OUTER JOIN "dim_date" ON "lineorder"."lo_orderdate" = "dim_date"."d_datekey"  WHERE "dim_date"."d_yearmonth" != 'Nov1994' AND "lineorder"."lo_supplycost" BETWEEN 55731 AND 70159 AND ("lineorder"."lo_commitdate" >= 19921840 OR "lineorder"."lo_custkey" BETWEEN 10531 AND 55140) AND "dim_date"."d_sellingseason" != 'Winter'