SELECT MIN("dim_date"."d_daynuminweek") as agg_0 FROM "dim_date"  WHERE ("dim_date"."d_lastdayinweekfl" >= 0 OR ("dim_date"."d_date" = 'July 30, 1992' AND "dim_date"."d_lastdayinweekfl" <= 0)) AND "dim_date"."d_dayofweek" IN ('Thursday', 'Tuesday', 'Wednesday', 'Sunday', 'Monday') AND "dim_date"."d_month" LIKE '%Dec%ember%' AND "dim_date"."d_yearmonth" != 'Mar1997' AND "dim_date"."d_sellingseason" NOT LIKE '%Summer%'