SELECT MAX("lineorder"."lo_custkey" + "lineorder"."lo_revenue") as agg_0, MIN("lineorder"."lo_quantity" + "lineorder"."lo_partkey") as agg_1 FROM "part" LEFT OUTER JOIN "lineorder" ON "part"."p_partkey" = "lineorder"."lo_partkey" LEFT OUTER JOIN "customer" ON "lineorder"."lo_custkey" = "customer"."c_custkey" LEFT OUTER JOIN "supplier" ON "lineorder"."lo_suppkey" = "supplier"."s_suppkey"  WHERE "lineorder"."lo_shippriority" != 0 AND "customer"."c_mktsegment" = 'BUILDING' AND "lineorder"."lo_orderpriority" NOT LIKE '%4%-NOT%'