SELECT MIN("customer"."c_custkey" + "lineorder"."lo_extendedprice") as agg_0, MIN("supplier"."s_suppkey") as agg_1, MIN("lineorder"."lo_shippriority" + "lineorder"."lo_partkey") as agg_2 FROM "part" LEFT OUTER JOIN "lineorder" ON "part"."p_partkey" = "lineorder"."lo_partkey" LEFT OUTER JOIN "supplier" ON "lineorder"."lo_suppkey" = "supplier"."s_suppkey" LEFT OUTER JOIN "customer" ON "lineorder"."lo_custkey" = "customer"."c_custkey"  WHERE ("supplier"."s_name" != 'Supplier#000001541' OR "supplier"."s_region" IN ('MIDDLE EAST', 'EUROPE', 'AMERICA', 'AFRICA') OR "supplier"."s_name" IN ('Supplier#000002664')) AND "lineorder"."lo_orderpriority" = '1-URGENT' AND "lineorder"."lo_shipmode" NOT LIKE '%S%HIP%'