SELECT SUM("lineorder"."lo_tax" + "lineorder"."lo_quantity") as agg_0, SUM("lineorder"."lo_custkey") as agg_1 FROM "supplier" LEFT OUTER JOIN "lineorder" ON "supplier"."s_suppkey" = "lineorder"."lo_suppkey" LEFT OUTER JOIN "customer" ON "lineorder"."lo_custkey" = "customer"."c_custkey"  WHERE "supplier"."s_suppkey" BETWEEN 1209 AND 3486 AND ("lineorder"."lo_shipmode" != 'AIR' OR "lineorder"."lo_shipmode" NOT LIKE '%SH%IP%' OR "lineorder"."lo_shipmode" != 'AIR')