SELECT SUM("IMG_OBJ"."H") as agg_0, COUNT(*) as agg_1, SUM("IMG_OBJ"."IMG_ID") as agg_2 FROM "OBJ_CLASSES" LEFT OUTER JOIN "IMG_OBJ" ON "OBJ_CLASSES"."OBJ_CLASS_ID" = "IMG_OBJ"."OBJ_CLASS_ID" LEFT OUTER JOIN "IMG_OBJ_ATT" ON "IMG_OBJ"."IMG_ID" = "IMG_OBJ_ATT"."IMG_ID" LEFT OUTER JOIN "ATT_CLASSES" ON "IMG_OBJ_ATT"."ATT_CLASS_ID" = "ATT_CLASSES"."ATT_CLASS_ID" LEFT OUTER JOIN "IMG_REL" ON "IMG_OBJ"."IMG_ID" = "IMG_REL"."IMG_ID"  WHERE ("ATT_CLASSES"."ATT_CLASS" = 'navy' OR "ATT_CLASSES"."ATT_CLASS" IN ('seated', 'airborne', 'vertical') OR "ATT_CLASSES"."ATT_CLASS" IN ('oval', 'building s', 'dark colored')) AND "ATT_CLASSES"."ATT_CLASS" NOT LIKE '%blue%' AND ("IMG_OBJ_ATT"."OBJ_SAMPLE_ID" >= 6 OR ("IMG_OBJ_ATT"."ATT_CLASS_ID" <= 632 AND "IMG_OBJ_ATT"."OBJ_SAMPLE_ID" BETWEEN 12 AND 14)) AND "IMG_OBJ"."IMG_ID" <= 4752610 AND "IMG_OBJ_ATT"."IMG_ID" BETWEEN 2353764 AND 2358459 AND "IMG_REL"."PRED_CLASS_ID" BETWEEN 149 AND 448