SELECT MAX("IMG_OBJ"."OBJ_CLASS_ID") as agg_0 FROM "PRED_CLASSES" LEFT OUTER JOIN "IMG_REL" ON "PRED_CLASSES"."PRED_CLASS_ID" = "IMG_REL"."PRED_CLASS_ID" LEFT OUTER JOIN "IMG_OBJ" ON "IMG_REL"."IMG_ID" = "IMG_OBJ"."IMG_ID"  WHERE "PRED_CLASSES"."PRED_CLASS" LIKE '%hanging%' AND "IMG_REL"."OBJ1_SAMPLE_ID" >= 10