SELECT SUM("IMG_OBJ"."OBJ_CLASS_ID") as agg_0, MIN("IMG_OBJ_ATT"."IMG_ID") as agg_1, AVG("IMG_OBJ"."X") as agg_2 FROM "OBJ_CLASSES" LEFT OUTER JOIN "IMG_OBJ" ON "OBJ_CLASSES"."OBJ_CLASS_ID" = "IMG_OBJ"."OBJ_CLASS_ID" LEFT OUTER JOIN "IMG_OBJ_ATT" ON "IMG_OBJ"."IMG_ID" = "IMG_OBJ_ATT"."IMG_ID" LEFT OUTER JOIN "ATT_CLASSES" ON "IMG_OBJ_ATT"."ATT_CLASS_ID" = "ATT_CLASSES"."ATT_CLASS_ID"  WHERE "ATT_CLASSES"."ATT_CLASS" != 'jet' AND "IMG_OBJ_ATT"."ATT_CLASS_ID" <= 1921 AND "ATT_CLASSES"."ATT_CLASS" IN ('outdoors', 'vertical', 'oval', 'airborne', 'dark colored', 'upside down', 'seated', 'building s') AND "ATT_CLASSES"."ATT_CLASS_ID" <= 599 AND "IMG_OBJ_ATT"."OBJ_SAMPLE_ID" >= 10 AND "IMG_OBJ_ATT"."IMG_ID" BETWEEN 2400959 AND 7166532