SELECT SUM("IMG_OBJ"."Y" + "IMG_OBJ"."X") as agg_0, AVG("IMG_OBJ_ATT"."ATT_CLASS_ID") as agg_1, SUM("IMG_OBJ"."X") as agg_2 FROM "ATT_CLASSES" LEFT OUTER JOIN "IMG_OBJ_ATT" ON "ATT_CLASSES"."ATT_CLASS_ID" = "IMG_OBJ_ATT"."ATT_CLASS_ID" LEFT OUTER JOIN "IMG_OBJ" ON "IMG_OBJ_ATT"."IMG_ID" = "IMG_OBJ"."IMG_ID"  WHERE "IMG_OBJ_ATT"."IMG_ID" BETWEEN 3450342 AND 7180433