SELECT MIN("IMG_REL"."PRED_CLASS_ID") as agg_0, SUM("IMG_OBJ"."OBJ_SAMPLE_ID") as agg_1 FROM "IMG_OBJ" LEFT OUTER JOIN "OBJ_CLASSES" ON "IMG_OBJ"."OBJ_CLASS_ID" = "OBJ_CLASSES"."OBJ_CLASS_ID" LEFT OUTER JOIN "IMG_OBJ_ATT" ON "IMG_OBJ"."IMG_ID" = "IMG_OBJ_ATT"."IMG_ID" LEFT OUTER JOIN "IMG_REL" ON "IMG_OBJ"."IMG_ID" = "IMG_REL"."IMG_ID" LEFT OUTER JOIN "ATT_CLASSES" ON "IMG_OBJ_ATT"."ATT_CLASS_ID" = "ATT_CLASSES"."ATT_CLASS_ID"  WHERE "OBJ_CLASSES"."OBJ_CLASS_ID" >= 222 AND ("IMG_OBJ"."IMG_ID" <= 1307023 OR ("IMG_OBJ"."IMG_ID" >= 4766275 AND "IMG_OBJ"."IMG_ID" >= 7205284)) AND ("ATT_CLASSES"."ATT_CLASS_ID" >= 761 OR "ATT_CLASSES"."ATT_CLASS_ID" <= 1948 OR "ATT_CLASSES"."ATT_CLASS_ID" BETWEEN 1098 AND 1702) AND "IMG_OBJ_ATT"."IMG_ID" <= 2459998