SELECT SUM("PRED_CLASSES"."PRED_CLASS_ID" + "IMG_REL"."IMG_ID") as agg_0, SUM("PRED_CLASSES"."PRED_CLASS_ID") as agg_1, COUNT(*) as agg_2 FROM "PRED_CLASSES" LEFT OUTER JOIN "IMG_REL" ON "PRED_CLASSES"."PRED_CLASS_ID" = "IMG_REL"."PRED_CLASS_ID" LEFT OUTER JOIN "IMG_OBJ" ON "IMG_REL"."IMG_ID" = "IMG_OBJ"."IMG_ID"  WHERE "IMG_OBJ"."W" >= 28 AND "IMG_OBJ"."H" BETWEEN 21 AND 751 AND "IMG_REL"."IMG_ID" <= 7219259